import React, { useState } from 'react';
import { useAppContext } from '../App';
import { useNavigate } from 'react-router-dom';

const LOGO_URL = 'data:image/png;base64,-Fioh-SjZ3D5VwAAAABJRU5ErkJggg==';

const ICONS = {
    logout: <svg xmlns="http://www.w.org/2000/svg" className="h-5 w-5 ml-2" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" /></svg>,
    sun: <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-yellow-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" /></svg>,
    moon: <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-indigo-300" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" /></svg>,
    collapse: <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" /></svg>,
    search: <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" /></svg>,
};


const ThemeToggle: React.FC = () => {
    const { theme, toggleTheme } = useAppContext();
    return (
        <button
            onClick={toggleTheme}
            className="p-2 rounded-full bg-gray-700/50 hover:bg-gray-600/50 text-white transition-colors duration-300"
            aria-label={`تغییر به تم ${theme === 'dark' ? 'روشن' : 'تاریک'}`}
        >
            {theme === 'dark' ? ICONS.sun : ICONS.moon}
        </button>
    );
};

const Header: React.FC = () => {
    const { logout, toggleSidebar } = useAppContext();
    const [headerQuery, setHeaderQuery] = useState('');
    const navigate = useNavigate();

    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
        const trimmedQuery = headerQuery.trim();
        if (trimmedQuery) {
            navigate(`/search?q=${encodeURIComponent(trimmedQuery)}`);
            setHeaderQuery('');
        }
    };


    return (
        <header className="bg-white dark:bg-gray-800/50 shadow-sm z-10">
            <div className="container mx-auto px-4 sm:px-6 py-3 flex justify-between items-center h-16 gap-4">
                 <div className="flex items-center">
                    {/* Desktop Collapse Button */}
                    <button onClick={toggleSidebar} className="hidden md:block p-2 text-gray-600 dark:text-gray-300">
                        {ICONS.collapse}
                    </button>
                </div>
                
                {/* Global Search Bar */}
                <div className="flex-1 hidden md:flex justify-center">
                    <form onSubmit={handleSearch} className="w-full max-w-md">
                        <div className="relative">
                            <input
                                type="search"
                                value={headerQuery}
                                onChange={(e) => setHeaderQuery(e.target.value)}
                                placeholder="جستجوی سریع بارنامه، راننده..."
                                className="w-full pl-10 pr-4 py-2 border dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 focus:ring-2 focus:ring-indigo-500 text-sm transition-all"
                            />
                            <button type="submit" className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-200" aria-label="جستجو">
                                {ICONS.search}
                            </button>
                        </div>
                    </form>
                </div>

                <div className="flex items-center space-x-4">
                    <ThemeToggle />
                    <button 
                        onClick={logout} 
                        className="flex items-center bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-lg transition-colors duration-300"
                    >
                        {ICONS.logout}
                        <span className="hidden sm:inline">خروج</span>
                    </button>
                </div>
            </div>
        </header>
    );
};

export default Header;